export interface ImageFile {
  id: string;
  file: File;
  status: 'pending' | 'compressing' | 'success' | 'error';
  originalSize: number;
  compressedSize?: number;
  compressedBlob?: Blob;
  error?: string;
  progress?: number;
}

export interface CompressionStats {
  totalFiles: number;
  compressed: number;
  failed: number;
  totalOriginalSize: number;
  totalCompressedSize: number;
}

const TINYPNG_API_KEY = 'tQzHVrC16K6Mr4kyyPsZzzy4Xk2BqrF4';
const TINYPNG_API_URL = 'https://api.tinify.com/shrink';

export async function compressImage(file: File): Promise<Blob> {
  try {
    // Upload image to TinyPNG
    const uploadResponse = await fetch(TINYPNG_API_URL, {
      method: 'POST',
      headers: {
        'Authorization': 'Basic ' + btoa('api:' + TINYPNG_API_KEY),
      },
      body: file,
    });

    if (!uploadResponse.ok) {
      const error = await uploadResponse.json();
      throw new Error(error.message || 'Compression failed');
    }

    const result = await uploadResponse.json();

    // Download compressed image
    const downloadResponse = await fetch(result.output.url);

    if (!downloadResponse.ok) {
      throw new Error('Failed to download compressed image');
    }

    return await downloadResponse.blob();
  } catch (error) {
    console.error('TinyPNG compression error:', error);
    throw error;
  }
}

export function formatFileSize(bytes: number): string {
  if (bytes === 0) return '0 Bytes';
  const k = 1024;
  const sizes = ['Bytes', 'KB', 'MB', 'GB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));
  return Math.round(bytes / Math.pow(k, i) * 100) / 100 + ' ' + sizes[i];
}

export function calculateSavings(original: number, compressed: number): number {
  if (original === 0) return 0;
  return Math.round((1 - compressed / original) * 100);
}

import { useState, useCallback } from 'react';
import { ImageFile, CompressionStats } from '../types/tinypng';
import { compressImage } from '../utils/tinypng';
import { v4 as uuidv4 } from 'uuid';
import { Upload, Download, Trash2, FolderOpen } from 'lucide-react';
import { Button } from './ui/button';
import { Card } from './ui/card';
import { Progress } from './ui/progress';
import { toast } from 'sonner';
import ImageList from './ImageList';
import StatsPanel from './StatsPanel';

const ImageCompressor = () => {
  const [images, setImages] = useState<ImageFile[]>([]);
  const [isCompressing, setIsCompressing] = useState(false);

  const handleFileSelect = useCallback((files: FileList | null) => {
    if (!files) return;

    const imageFiles: ImageFile[] = [];
    for (let i = 0; i < files.length; i++) {
      const file = files[i];
      if (file.type.startsWith('image/')) {
        imageFiles.push({
          id: uuidv4(),
          file,
          status: 'pending',
          originalSize: file.size,
        });
      }
    }

    if (imageFiles.length === 0) {
      toast.error('请选择有效的图片文件');
      return;
    }

    setImages((prev) => [...prev, ...imageFiles]);
    toast.success(`已添加 ${imageFiles.length} 个图片`);
  }, []);

  const handleFolderSelect = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    handleFileSelect(e.target.files);
  }, [handleFileSelect]);

  const handleFileInputChange = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    handleFileSelect(e.target.files);
  }, [handleFileSelect]);

  const handleDrop = useCallback((e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();

    const files = e.dataTransfer.files;
    handleFileSelect(files);
  }, [handleFileSelect]);

  const handleDragOver = useCallback((e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
  }, []);

  const compressImages = async () => {
    const pendingImages = images.filter((img) => img.status === 'pending');
    if (pendingImages.length === 0) {
      toast.error('没有待压缩的图片');
      return;
    }

    setIsCompressing(true);

    for (const image of pendingImages) {
      try {
        setImages((prev) =>
          prev.map((img) =>
            img.id === image.id ? { ...img, status: 'compressing', progress: 0 } : img
          )
        );

        const compressedBlob = await compressImage(image.file);

        setImages((prev) =>
          prev.map((img) =>
            img.id === image.id
              ? {
                  ...img,
                  status: 'success',
                  compressedSize: compressedBlob.size,
                  compressedBlob,
                  progress: 100,
                }
              : img
          )
        );
      } catch (error) {
        setImages((prev) =>
          prev.map((img) =>
            img.id === image.id
              ? {
                  ...img,
                  status: 'error',
                  error: error instanceof Error ? error.message : '压缩失败',
                }
              : img
          )
        );
      }
    }

    setIsCompressing(false);
    toast.success('压缩完成！');
  };

  const downloadAll = () => {
    const successImages = images.filter((img) => img.status === 'success' && img.compressedBlob);

    if (successImages.length === 0) {
      toast.error('没有可下载的图片');
      return;
    }

    successImages.forEach((image) => {
      if (!image.compressedBlob) return;

      const url = URL.createObjectURL(image.compressedBlob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `compressed_${image.file.name}`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
    });

    toast.success(`已下载 ${successImages.length} 个文件`);
  };

  const clearAll = () => {
    setImages([]);
    toast.success('已清空列表');
  };

  const removeImage = (id: string) => {
    setImages((prev) => prev.filter((img) => img.id !== id));
  };

  const stats: CompressionStats = {
    totalFiles: images.length,
    compressed: images.filter((img) => img.status === 'success').length,
    failed: images.filter((img) => img.status === 'error').length,
    totalOriginalSize: images.reduce((sum, img) => sum + img.originalSize, 0),
    totalCompressedSize: images.reduce(
      (sum, img) => sum + (img.compressedSize || 0),
      0
    ),
  };

  return (
    <div className="min-h-screen bg-background p-4 md:p-8">
      <div className="max-w-7xl mx-auto space-y-6">
        <div className="text-center space-y-2">
          <h1 className="text-4xl font-bold text-foreground">图片压缩工具</h1>
          <p className="text-muted-foreground">使用 TinyPNG API 进行高质量图片压缩</p>
        </div>

        <Card className="p-8">
          <div
            onDrop={handleDrop}
            onDragOver={handleDragOver}
            className="border-2 border-dashed border-border rounded-lg p-12 text-center hover:border-primary transition-colors cursor-pointer"
          >
            <Upload className="w-12 h-12 mx-auto mb-4 text-muted-foreground" />
            <p className="text-lg font-medium text-foreground mb-2">
              拖拽图片到此处，或点击选择文件
            </p>
            <p className="text-sm text-muted-foreground mb-4">
              支持 PNG、JPG、JPEG、WebP 格式
            </p>
            <div className="flex gap-4 justify-center">
              <Button asChild variant="outline">
                <label className="cursor-pointer">
                  <Upload className="w-4 h-4 mr-2" />
                  选择文件
                  <input
                    type="file"
                    multiple
                    accept="image/*"
                    onChange={handleFileInputChange}
                    className="hidden"
                  />
                </label>
              </Button>
              <Button asChild variant="outline">
                <label className="cursor-pointer">
                  <FolderOpen className="w-4 h-4 mr-2" />
                  选择文件夹
                  <input
                    type="file"
                    {...({ webkitdirectory: '', directory: '' } as any)}
                    multiple
                    onChange={handleFolderSelect}
                    className="hidden"
                  />
                </label>
              </Button>
            </div>
          </div>
        </Card>

        {images.length > 0 && (
          <>
            <StatsPanel stats={stats} />

            <div className="flex gap-4 justify-end">
              <Button
                onClick={compressImages}
                disabled={isCompressing || images.every((img) => img.status !== 'pending')}
                size="lg"
              >
                {isCompressing ? '压缩中...' : '开始压缩'}
              </Button>
              <Button
                onClick={downloadAll}
                disabled={stats.compressed === 0}
                variant="outline"
                size="lg"
              >
                <Download className="w-4 h-4 mr-2" />
                下载全部
              </Button>
              <Button onClick={clearAll} variant="destructive" size="lg">
                <Trash2 className="w-4 h-4 mr-2" />
                清空列表
              </Button>
            </div>

            <ImageList images={images} onRemove={removeImage} />
          </>
        )}
      </div>
    </div>
  );
};

export default ImageCompressor;

import { ImageFile } from '../types/tinypng';
import { Card } from './ui/card';
import { Button } from './ui/button';
import { Progress } from './ui/progress';
import { X, Download, CheckCircle2, XCircle, Loader2 } from 'lucide-react';
import { formatFileSize, calculateSavings } from '../utils/tinypng';

interface ImageListProps {
  images: ImageFile[];
  onRemove: (id: string) => void;
}

const ImageList = ({ images, onRemove }: ImageListProps) => {
  const downloadImage = (image: ImageFile) => {
    if (!image.compressedBlob) return;

    const url = URL.createObjectURL(image.compressedBlob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `compressed_${image.file.name}`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  return (
    <div className="space-y-4">
      <h2 className="text-2xl font-semibold text-foreground">图片列表</h2>
      <div className="grid gap-4">
        {images.map((image) => {
          const savings = image.compressedSize
            ? calculateSavings(image.originalSize, image.compressedSize)
            : 0;

          return (
            <Card key={image.id} className="p-4">
              <div className="flex items-center gap-4">
                <div className="flex-shrink-0 w-16 h-16 rounded-lg overflow-hidden bg-muted">
                  <img
                    src={URL.createObjectURL(image.file)}
                    alt={image.file.name}
                    className="w-full h-full object-cover"
                  />
                </div>

                <div className="flex-1 min-w-0">
                  <p className="font-medium text-foreground truncate">
                    {image.file.name}
                  </p>
                  <div className="flex items-center gap-4 mt-1 text-sm text-muted-foreground">
                    <span>{formatFileSize(image.originalSize)}</span>
                    {image.compressedSize && (
                      <>
                        <span>→</span>
                        <span className="text-success">
                          {formatFileSize(image.compressedSize)}
                        </span>
                        <span className="text-success font-medium">
                          节省 {savings}%
                        </span>
                      </>
                    )}
                  </div>

                  {image.status === 'compressing' && (
                    <div className="mt-2">
                      <Progress value={image.progress || 0} className="h-2" />
                    </div>
                  )}

                  {image.error && (
                    <p className="mt-2 text-sm text-destructive">{image.error}</p>
                  )}
                </div>

                <div className="flex items-center gap-2">
                  {image.status === 'pending' && (
                    <div className="w-6 h-6 rounded-full bg-muted flex items-center justify-center">
                      <div className="w-2 h-2 rounded-full bg-muted-foreground" />
                    </div>
                  )}
                  {image.status === 'compressing' && (
                    <Loader2 className="w-6 h-6 text-primary animate-spin" />
                  )}
                  {image.status === 'success' && (
                    <>
                      <CheckCircle2 className="w-6 h-6 text-success" />
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => downloadImage(image)}
                      >
                        <Download className="w-4 h-4" />
                      </Button>
                    </>
                  )}
                  {image.status === 'error' && (
                    <XCircle className="w-6 h-6 text-destructive" />
                  )}
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={() => onRemove(image.id)}
                  >
                    <X className="w-4 h-4" />
                  </Button>
                </div>
              </div>
            </Card>
          );
        })}
      </div>
    </div>
  );
};

export default ImageList;

